## 1. lib/trpc.ts — tRPC client setup for mobile
```typescript
import { createTRPCReact } from "@trpc/react-query";
import { httpBatchLink, loggerLink } from "@trpc/client";
import superjson from "superjson";

// Import AppRouter type from the web app
// In the monorepo, we reference it via the shared api package path
import type { AppRouter } from "../../packages/api/src/index";

export const trpc = createTRPCReact<AppRouter>();

const getBaseUrl = () => {
  // Use environment variable for API URL
  const url = process.env.EXPO_PUBLIC_API_URL;
  if (url) return url;
  // Default to localhost for development
  return "http://localhost:3002";
};

export function getTRPCClient(getToken: () => Promise<string | null>) {
  return trpc.createClient({
    links: [
      loggerLink({
        enabled: (opts) =>
          __DEV__ || (opts.direction === "down" && opts.result instanceof Error),
      }),
      httpBatchLink({
        transformer: superjson,
        url: `${getBaseUrl()}/api/trpc`,
        async headers() {
          const token = await getToken();
          return {
            Authorization: token ? `Bearer ${token}` : "",
          };
        },
      }),
    ],
  });
}
```

## 2. lib/auth.ts — Clerk token cache using expo-secure-store
```typescript
import * as SecureStore from "expo-secure-store";
import { Platform } from "react-native";
import type { TokenCache } from "@clerk/clerk-expo";

const createTokenCache = (): TokenCache => {
  return {
    getToken: async (key: string) => {
      try {
        const item = await SecureStore.getItemAsync(key);
        if (item) {
          console.log(`${key} was used 🔐 \n`);
        } else {
          console.log("No values stored under key: " + key);
        }
        return item;
      } catch (error) {
        console.error("SecureStore get item error: ", error);
        await SecureStore.deleteItemAsync(key);
        return null;
      }
    },
    saveToken: (key: string, value: string) => {
      return SecureStore.setItemAsync(key, value);
    },
  };
};

// SecureStore is not available on web
export const tokenCache = Platform.OS !== "web" ? createTokenCache() : undefined;
```

## 3. providers/TRPCProvider.tsx — Provider that wraps the app with tRPC + React Query
```typescript
import React, { useState } from "react";
import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
import { useAuth } from "@clerk/clerk-expo";
import { trpc, getTRPCClient } from "../lib/trpc";

export function TRPCProvider({ children }: { children: React.ReactNode }) {
  const { getToken } = useAuth();

  const [queryClient] = useState(
    () =>
      new QueryClient({
        defaultOptions: {
          queries: {
            staleTime: 5 * 60 * 1000,
            retry: 2,
          },
        },
      })
  );

  const [trpcClient] = useState(() => getTRPCClient(getToken));

  return (
    <trpc.Provider client={trpcClient} queryClient={queryClient}>
      <QueryClientProvider client={queryClient}>{children}</QueryClientProvider>
    </trpc.Provider>
  );
}
```

