export default function FlockDetailScreen() {
  const { flockId } = useLocalSearchParams<{ flockId: string }>();
  const router = useRouter();

  const {
    data: flock,
    isLoading: flockLoading,
    refetch: refetchFlock,
  } = trpc.flocks.getFlock.useQuery({ id: flockId! });

  const { data: stats, isLoading: statsLoading } =
    trpc.stats.getFlockStats.useQuery({ flockId: flockId! });

  const { data: breeds } = trpc.breeds.getBreeds.useQuery({
    flockId: flockId!,
  });

  const isLoading = flockLoading || statsLoading;
  const [refreshing, setRefreshing] = React.useState(false);

  const onRefresh = async () => {
    setRefreshing(true);
    await refetchFlock();
    setRefreshing(false);
  };

  if (isLoading) {
    return (
      <View style={styles.center}>
        <ActivityIndicator size="large" color={colors.primary} />
      </View>
    );
  }

  if (!flock) {
    return (
      <View style={styles.center}>
        <Text style={styles.errorText}>Flock not found</Text>
      </View>
    );
  }

  return (
    <>
      <Stack.Screen options={{ title: flock.name }} />
      <ScrollView
        style={styles.container}
        contentContainerStyle={styles.content}
        refreshControl={
          <RefreshControl
            refreshing={refreshing}
            onRefresh={onRefresh}
            tintColor={colors.primary}
          />
        }
      >
        {flock.image ? (
          <Image
            source={{ uri: flock.image }}
            style={styles.heroImage}
            contentFit="cover"
          />
        ) : null}

        <View style={styles.header}>
          <Text style={styles.title}>{flock.name}</Text>
          {flock.description ? (
            <Text style={styles.description}>{flock.description}</Text>
          ) : null}
          <TouchableOpacity
            style={styles.editButton}
            onPress={() => router.push(`/(app)/flocks/${flockId}/edit`)}
          >
            <Text style={styles.editButtonText}>Edit</Text>
          </TouchableOpacity>
        </View>

        {stats ? (
          <View style={styles.statsGrid}>
            <StatCard label="Today" value={stats.today ?? 0} />
            <StatCard label="This Week" value={stats.week ?? 0} />
            <StatCard label="This Month" value={stats.month ?? 0} />
            <StatCard label="Breeds" value={breeds?.length ?? 0} />
          </View>
        ) : null}

        {breeds && breeds.length > 0 ? (
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Breeds</Text>
            {breeds.map((breed) => (
              <View key={breed.id} style={styles.breedRow}>
                {breed.image ? (
                  <Image
                    source={{ uri: breed.image }}
                    style={styles.breedImage}
                    contentFit="cover"
