"use client";

import { useState } from "react";
import { trpc } from "../../utils/trpc";
import { MdOutlineEditNote } from "react-icons/md";
import toast from "react-hot-toast";
import { RiLoader4Fill } from "react-icons/ri";
import { handleTimezone } from "./date-utils";
import { format, startOfDay } from "date-fns";
import { useRouter } from "next/navigation";
import { Button } from "@components/ui/button";
import { Input } from "@components/ui/input";
import { Textarea } from "@components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@components/ui/select";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@components/ui/popover";
import { cn } from "@lib/utils";
import { CalendarIcon } from "@radix-ui/react-icons";
import { Calendar } from "@components/ui/calendar";
import { ResponsiveDialog, DrawerClose } from "@components/ui/responsive-dialog";

const LogModal = ({ flockId }: { flockId: string | undefined }) => {
  const [open, setOpen] = useState(false);
  const [date, setDate] = useState<Date>(new Date());
  const [count, setCount] = useState<number>();
  const [notes, setNotes] = useState<string>();
  const [breedId, setBreedId] = useState<string>();

  const router = useRouter();

  const utils = trpc.useUtils();

  const { mutateAsync: createLogMutation, isLoading } =
    trpc.logs.createLog.useMutation({
      onSuccess: () => {
        utils.stats.getStats.invalidate();
        utils.stats.getExpenseStats.invalidate();
        router.refresh();
        toast.success("New log created!");
      },
    });

  const flockQuery = trpc.flocks.getFlock.useQuery({
    flockId: flockId as string,
  });

  function closeModal(): void {
    setOpen(false);
  }

  function resetFormValues(): void {
    setDate(new Date());
    setNotes(undefined);
    setCount(undefined);
    setBreedId(undefined);
  }

  async function createNewLog(
    flockId: string,
    date: Date,
    count: number,
    notes?: string,
  ): Promise<void> {
    await createLogMutation({
      flockId,
      date: startOfDay(date),
      count,
      notes,
      breedId,
    });
    closeModal();
    resetFormValues();
  }

  if (!flockId) {
    return null;
  }

  return (
    <ResponsiveDialog
      open={open}
      onOpenChange={setOpen}
      trigger={
        <Button
          className="mb-1 mr-1 h-10 w-full rounded px-4 py-2 shadow outline-none transition-all focus:outline-none"
          variant={"secondary"}
        >
          <MdOutlineEditNote className="text-2xl" />
          &nbsp;Log Eggs
        </Button>
      }
      title="Log Eggs"
      footer={
        <>
          <DrawerClose asChild>
            <Button variant="ghost" onClick={closeModal}>
              Cancel
            </Button>
          </DrawerClose>
          <Button
            variant="secondary"
            disabled={isLoading}
            onClick={async () => {
              if (date && count) {
                await createNewLog(
                  flockId,
                  date,
                  count,
                  notes,
                );
              }
            }}
          >
            {isLoading ? (
              <RiLoader4Fill className="animate-spin text-2xl" />
            ) : (
              "Submit"
            )}
          </Button>
        </>
      }
    >
      <form
        className="flex w-full flex-col gap-4 p-4"
        onSubmit={async (e) => {
          e.preventDefault();
          if (date && count) {
            await createNewLog(
              flockId,
              date,
              count,
              notes,
            );
          }
        }}
      >
        <Popover>
          <PopoverTrigger asChild>
            <Button
              variant={"outline"}
              className={cn(
                "h-12 w-full justify-start bg-white text-left font-normal",
                !date && "text-muted-foreground",
              )}
            >
              <CalendarIcon className="mr-2 h-4 w-4" />
              {date ? (
                format(date, "PPP")
