  UserButton,
  useUser,
} from "@clerk/nextjs";


import { inferRouterOutputs } from "@trpc/server";
import { trpc } from "@utils/trpc";
import { motion, HTMLMotionProps } from "framer-motion";
import { usePathname } from "next/navigation";
import { ReactElement, useRef, useState } from "react";
import { Toaster } from "react-hot-toast";
import { AppRouter } from "src/server/trpc/router/_app";
import logo from "../../../public/FlockNerd-logo-v2.png";
import NotificationsList from "./NotificationsList";
import { Button } from "@components/ui/button";

type RouterOutput = inferRouterOutputs<AppRouter>;
type GetUserNotifications = RouterOutput["auth"]["getUserNotifications"];

// Top navbar
export default function AppLayout({
  children,
  initialNotifications,
}: {
  children: React.ReactNode;
  initialNotifications: GetUserNotifications;
}) {
  const { user } = useUser();

  const pathName = usePathname();
  const [sideBarOpen, setSideBarOpen] = useState(false);
  const [notificationsOpen, setNotificationsOpen] = useState(false);
  const genericHamburgerLine = `h-[2px] w-[22px] my-[2.5px] rounded-full bg-[#FEF9F6] transition ease transform duration-300`;
  const links = [
    {
      icon: <MdHomeFilled className="mr-5 mt-[-3px] inline text-2xl" />,
      title: "My Flocks",
      path: "/app/flocks",
      onClick: () => setSideBarOpen(false),
    },
    {
      icon: <MdOutlineEditNote className="mr-5 mt-[-3px] inline text-2xl" />,
      title: "All Logs",
      path: "/app/logs",
      onClick: () => setSideBarOpen(false),
    },
    {
      icon: <AiOutlineDollar className="mr-5 mt-[-3px] inline text-2xl" />,
      title: "All Expenses",
      path: "/app/expenses",
      onClick: () => setSideBarOpen(false),
    },
    {
      icon: <MdManageAccounts className="mr-5 mt-[-3px] inline text-2xl" />,
      title: "Account Settings",
      path: "/app/settings",
      onClick: () => setSideBarOpen(false),
    },
  ];

  const notificationsButtonRef = useRef<HTMLButtonElement | null>(null);

  const focusNotificationsButton = () => {
    if (notificationsButtonRef.current) {
      notificationsButtonRef.current.focus();
    }
  };

  const { data: notifications } = trpc.auth.getUserNotifications.useQuery(
    undefined,
    {
      initialData: initialNotifications,
    },
  );

  // Cast notifications to the correct type
  const typedNotifications = notifications as unknown as {
    id: string;
    title: string;
    message: string;
    date?: string;
    read: boolean;
    readDate?: string | null;
    userId: string;
    link: string;
    action?: string;
  }[];

  // Function to safely count unread notifications
  const countUnreadNotifications = () => {
    if (!typedNotifications || !Array.isArray(typedNotifications)) return 0;
    return typedNotifications.filter(not => not && not.read === false).length;
  };

  return (
    <>
      <Toaster position="top-right" />
      <nav className="navbar z-30 h-15 pl-2 pr-3 lg:h-16 lg:pl-6">
        <ul>
          <li className="inline lg:hidden">
            <Button
              variant="ghost"
              id="menuButton"
              aria-haspopup="true"
              aria-expanded={sideBarOpen}
              aria-controls="sideNav"
              aria-label="Open mobile nav menu"
              className="group flex w-12 flex-col items-center justify-center rounded hover:bg-slate-400/10"
              onClick={() => {
                setSideBarOpen(!sideBarOpen);
              }}
            >
              <div
                className={`${genericHamburgerLine} ${
                  sideBarOpen
                    ? "translate-y-[7px] rotate-45 group-hover:opacity-100"
                    : "group-hover:opacity-100"
                }`}
              />
              <div
                className={`${genericHamburgerLine} ${
                  sideBarOpen ? "opacity-0" : "group-hover:opacity-100"
                }`}
              />
              <div
                className={`${genericHamburgerLine} ${
                  sideBarOpen
                    ? "-translate-y-[7px] -rotate-45 group-hover:opacity-100"
                    : "group-hover:opacity-100"
                }`}
              />
            </Button>
          </li>
          <li className="hidden translate-x-2 cursor-pointer sm:translate-x-0 md:block">
            <Link href="/" aria-label="Go to the homepage">
              <span className="flex items-center">
                <Image
                  src={logo}
                  height="55"
                  alt="Flock Nerd logo the white outline of a chicken"
                  aria-hidden="true"
                />
              </span>
            </Link>
          </li>

          {/* user is signed-in */}
          {user && (
            <>
              <li
                className={`mr-2 flex items-center gap-2 rounded transition-all`}
              >
                <Button
                  variant="ghost"
                  ref={notificationsButtonRef}
                  aria-haspopup="true" // indicates that this button triggers a popover
                  aria-expanded={notificationsOpen} // indicates whether the popover is open or closed
                  aria-label={`Toggle notifications - ${countUnreadNotifications()} unread notifications`} // provides a descriptive name for the button
                  role="button"
                  type="button"
                  className={`animate__animated animate__fadeInRight relative cursor-pointer px-3 py-3 hover:bg-slate-400/10 hover:text-white ${
                    notificationsOpen ? "open" : ""
                  }`}
                  onClick={() => {
                    setNotificationsOpen(!notificationsOpen);
                  }}
                >
                  <MdNotifications aria-hidden className="text-2xl" />
                  <div
                    className={`${
                      countUnreadNotifications() > 0 ? "opacity-100" : "opacity-0"
                    } absolute right-1 top-0 inline-flex h-5 w-5 items-center justify-center rounded bg-red-500 text-[0.6rem] font-bold text-white dark:border-gray-900`}
                  >
                    {countUnreadNotifications()}
                  </div>
                </Button>
                {/* <div className="user-name animate__animated animate__fadeInLeft hidden lg:block">
                  {user.fullName}
                </div> */}
                <UserButton
                  afterSignOutUrl="/"
                  userProfileMode="navigation"
                  userProfileUrl="/app/settings/"
                  appearance={{
                    elements: {
                      userButtonOuterIdentifier:
                        "text-white font-bold text-base ml-2",
                      userButtonPopoverCard:
                        "bg-[#FEF9F6] rounded-lg text-primary",
                      // userButtonTrigger: "ml-2 md:ml-1",
                      avatarBox: "h-9 w-9 lg:h-10 lg:w-10",
                      avatarImage: "border-2 border-white rounded-full",
                    },
                  }}
                />
              </li>
            </>
          )}

          {/* user is not signed-in */}
          {!user && (
            <li>
              <SignInButton>
                <button className="rounded border-2 bg-transparent px-2 py-2 pr-3 transition-all hover:bg-white hover:text-primary">
                  <MdLogin />
                  &nbsp;Sign in
                </button>
              </SignInButton>
            </li>
          )}
        </ul>
      </nav>
      <section
        className={
          sideBarOpen
            ? "h-full overflow-y-auto transition-all lg:ml-52 lg:h-full"
            : "h-full overflow-y-auto transition-all lg:ml-52 lg:h-full"
        }
      >
        {children}
      </section>
      <aside
        aria-label="Notifications menu"
        aria-hidden={!notificationsOpen}
        className={`fadeIn pb-safe fixed bottom-0 right-0 top-[60px] w-80 overflow-y-auto bg-[#FEF9F6] p-3 shadow-2xl transition-all lg:top-[65px] lg:pb-3 ${
          notificationsOpen ? "translate-x-0" : "translate-x-80"
        }`}
      >
        {typedNotifications?.length ? (
          <NotificationsList
            notifications={typedNotifications}
            notificationsOpen={notificationsOpen}
            closeMenu={() => {
              setNotificationsOpen(false);
              focusNotificationsButton();
            }}
          />
        ) : (
          <p>No notifications to display.</p>
        )}
      </aside>
      <aside
        className={
          sideBarOpen
            ? "fixed top-[60px] h-full w-52 overflow-y-auto bg-[#FEF9F6] shadow-2xl transition-transform lg:top-[65px] lg:h-full"
            : "fixed top-[60px] h-full w-52 -translate-x-52 overflow-y-auto bg-[#FEF9F6] shadow-lg transition-transform lg:top-[65px] lg:h-full lg:translate-x-0"
        }
      >
        <ul className="side-nav pt-7">
          {links.map((link, index) => {
            return <SidebarNavLink {...link} key={index} />;
          })}
          <li
            className={`mb-0 px-2 ${
              pathName == "/logout" ? "bg-gray-400 text-white" : ""
            } hover:text-gray-500`}
          >
            <SignOutButton>
              <button className="flex items-center px-2 py-3">
                <MdLogout className="ml-[2px] mr-[18.5px] inline text-2xl" />
                Logout
              </button>
            </SignOutButton>
          </li>
        </ul>
      </aside>
    </>
  );
}

function SidebarNavLink({
  icon,
  title,
  path,
  onClick,
}: {
  icon: ReactElement;
  title: string;
  path: string;
  onClick: () => void;
}) {
  const pathName = usePathname();
  return (
    <>
      {title == "Account Settings" && (
        <li className="mt-auto px-3">
          <div className="divider my-3 dark:border-t-gray-500"></div>
        </li>
      )}
      <li
        key={title}
        className={`mb-0 px-2 ${
          pathName?.startsWith(path) ? "text-white" : "hover:text-gray-500"
        } relative`}
        style={{
          transition: `color 0.2s ease-in-out ${
            pathName?.startsWith(path) ? "0.15s" : "0s"
          }`,
        }}
      >
        <Link
          href={path}
          className="relative z-10 flex items-center px-2 py-3"
          onClick={onClick}
        >
          <>
            {icon}
            {title}
          </>
        </Link>
        {/* <AnimatePresence mode="wait" initial={false}> */}
        {pathName?.startsWith(path) && (
          <div className="absolute left-0 top-0 z-0 h-full w-full origin-left bg-[#CD7660]">
            {/* Specify HTMLMotionProps explicitly to help TypeScript */}
            {(() => {
              const motionProps: HTMLMotionProps<"div"> = {
                layoutId: "highlight",
                transition: { type: "spring", bounce: 0.2, duration: 0.6 }
              };
              return <motion.div {...motionProps} />;
            })()}
          </div>
        )}
        {/* </AnimatePresence> */}
      </li>
    </>
  );
}

"use client";

import { motion } from "framer-motion";
import Image from "next/image";
import { useRouter } from "next/navigation";
import { useCallback } from "react";

import Breeds from "@components/breeds/Breeds";
import ExpenseModal from "@components/flocks/ExpenseModal";
import LogModal from "@components/flocks/LogModal";
import Stats from "@components/flocks/Stats";
import Card from "@components/shared/Card";
import Loader from "@components/shared/Loader";

import EditModal from "@components/flocks/EditModal";
import type { Breed, Flock } from "@lib/db/schema-postgres";
import { useFlockDataAppDir } from "@lib/hooks";
import { format, subDays } from "date-fns";
import { usePathname, useSearchParams } from "next/navigation";

export const runtime = "nodejs";

const Flock = ({ userId, flockId }: { userId: string; flockId: string }) => {
  const router = useRouter();
  const searchParams = useSearchParams();
  const path = usePathname();
  const statsRange =
    searchParams?.get("statsRange") ||
    `${format(subDays(new Date(), 7), "yyyy-MM-dd")},${format(
      new Date(),
      "yyyy-MM-dd",
    )}`;
  const expenseMonths = searchParams?.get("expenseMonths")
    ? parseInt(
        searchParams.get("expenseMonths") !== null
          ? (searchParams.get("expenseMonths") as string)
          : "6",
      )
    : 6;
  const { flock, stats, range, breedStats } = useFlockDataAppDir(
    userId,
    flockId,
    statsRange,
    searchParams?.get("breedFilter"),
    expenseMonths,
  );

  const onRangeChange = useCallback(
    (event: any) => {
      const curParams = new URLSearchParams(searchParams || "");

      curParams.set("statsRange", event.target.value.toString());

      router.replace(`${path}?${curParams.toString()}`);
    },
    [router, searchParams, path],
  );

  const onMonthsChange = useCallback(
    (value: string) => {
      const curParams = new URLSearchParams(searchParams || "");

      curParams.set("expenseMonths", value.toString());

      router.replace(`${path}?${curParams.toString()}`);
    },
    [router, searchParams, path],
  );

  const clearFilter = useCallback(() => {
    const curParams = new URLSearchParams(searchParams || "");
    curParams.delete("breedFilter");

    router.replace(`${path}?${curParams.toString()}`);
  }, [router, searchParams, path]);

  const filterBreed = flock?.breeds.find(
    (breed) => breed.id == (searchParams?.get("breedFilter") as string),
  );

  if (!flock) {
    return <LoaderLayout />;
  }

  return (
    <FlockLayout
      flock={flock}
      flockId={flockId}
      onRangeChange={onRangeChange}
      onMonthsChange={onMonthsChange}
      range={range}
      clearFilter={clearFilter}
      filterText={filterBreed?.name || filterBreed?.breed}
      filterId={searchParams?.get("breedFilter") as string}
      stats={stats}
      breedStats={breedStats}
      userId={userId}
      expenseMonths={expenseMonths}
    />
  );
};

const LoaderLayout = () => (
  <main className="flex h-full min-h-[75vh] flex-col justify-center p-0 lg:p-8 lg:px-[3.5vw]">
    <div className="flex h-full items-center justify-center">
      <Loader show={true} />
    </div>
  </main>
);

const FlockLayout = ({
  flock,
  flockId,
  onRangeChange,
  onMonthsChange,
  range,
  clearFilter,
  filterText,
  filterId,
  stats,
  breedStats,
  userId,
  expenseMonths,
}: {
  flock: Flock & { breeds: Breed[]; tasks?: any[] };
  flockId: string;
  onRangeChange: (event: any) => void;
  onMonthsChange: (value: string) => void;
  range: {
    from: Date;
    to: Date;
  };
  clearFilter: () => void;
  filterText: string | undefined;
  filterId: string;
  stats: {
    expenses?: any;
    logs?: any;
    lastWeekAvg?: any;
    thisWeekAvg?: any;
  };
  breedStats?: any;
  userId: string;
  expenseMonths?: number;
}) => (
  <main className="flex flex-col justify-center p-0 lg:p-8 lg:px-[3.5vw]">
    <div className="shadow-xl">
      <Card title="Flock Details" className="" key={flockId?.toString()}>
        {/* <EditLink flockId={flockId} /> */}
        <FlockActions flockId={flockId?.toString()} userId={userId} />
        <FlockInfo flock={flock} flockId={flockId} />
        {/* <TaskList tasks={flock?.tasks} flockId={flockId} userId={userId} /> */}
        <Stats
          stats={stats}
          flock={flock}
          className="mt-4 basis-full xl:basis-[75%]"
          range={range}
          onRangeChange={onRangeChange}
          onMonthsChange={onMonthsChange}
          filter={filterText}
          filterId={filterId}
          clearFilter={clearFilter}
          expenseMonths={expenseMonths}
        />
        <Breeds
          flockId={flockId?.toString()}
          breeds={flock?.breeds}
          top={breedStats?.at(0)?.breedId}
          className="mt-4 basis-full xl:basis-[23%]"
          userId={userId}
        />
      </Card>
    </div>
  </main>
);

const FlockActions = ({
  flockId,
  userId,
}: {
  flockId: string;
  userId: string;
}) => (
  <div className="absolute right-0 top-0 mr-5 mt-3 flex self-start md:mt-2">
    {/* <AddTaskModal userId={userId} flockId={flockId} /> */}
    <EditModal userId={userId} flockId={flockId} />
  </div>
);

const FlockInfo = ({ flock, flockId }: any) => (
  <motion.div
    initial={{ opacity: 0, translateY: 10 }}
    animate={{ opacity: 1, translateY: 0 }}
    transition={{ duration: 0.5 }}
  >
    <div className="flex flex-wrap items-center">
      <Image
        src={flock?.imageUrl}
        width="150"
        height="150"
        className="flock-image aspect-square object-cover"
        alt="A user uploaded image that represents this flock"
      />
      <FlockDetails flock={flock} />
      <Actions flockId={flockId} />
    </div>
  </motion.div>
);

const FlockDetails = ({ flock }: any) => (
  <div className="ml-0 sm:ml-6">
    <div className="flex items-center">
      <h1 className="mr-3 dark:text-gray-300">{flock?.name}</h1>
    </div>
    <p className="description dark:text-gray-300">{flock?.description}</p>
    <p className="mt-2 text-gray-400 dark:text-gray-400">{flock?.type}</p>
  </div>
);

const Actions = ({ flockId }: any) => (
  <div className="ml-0 mt-4 flex w-full flex-wrap self-start lg:ml-auto lg:mt-0 lg:w-auto">
    <LogModal flockId={flockId?.toString()} />
    <div className="p-1"></div>
    <ExpenseModal flockId={flockId?.toString()} />
  </div>
);

export default Flock;

