  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@components/ui/select";
import { Textarea } from "@components/ui/textarea";
import { ResponsiveDialog, DialogClose } from "@components/ui/responsive-dialog";

const ExpenseModal = ({ flockId }: { flockId: string | undefined }) => {
  const [showModal, setShowModal] = useState(false);
  const [date, setDate] = useState<Date>(new Date());
  const [amount, setAmount] = useState(0);
  const [memo, setMemo] = useState<string>();
  const [category, setCategory] = useState<string>("feed");

  const utils = trpc.useUtils();

  const { mutateAsync: createExpense, isPending } =
    trpc.expenses.createExpense.useMutation({
      onSuccess: () => {
        utils.stats.getExpenseStats.invalidate();
        toast.success("Expense added!");
      },
    });

  function closeModal(): void {
    setShowModal(false);
  }

  function resetFormValues(): void {
    setDate(new Date());
    setMemo(undefined);
    setAmount(0);
  }

  async function createNewLog(
    flockId: string,
    date: Date,
    amount: number,
    category: string,
    memo?: string,
  ): Promise<void> {
    await createExpense({
      flockId,
      date: startOfDay(date),
      amount,
      category,
      memo,
    });
    closeModal();
    resetFormValues();
  }

  if (!flockId) {
    return null;
  }

  const handleSubmit = async () => {
    if (date && amount) {
      await createNewLog(
        flockId,
        date,
        amount,
        category ? category : "other",
        memo,
      );
    }
  };

  const trigger = (
    <Button
      className="mb-1 mr-1 h-10 w-full rounded px-4 py-2 shadow outline-none transition-all focus:outline-none"
      type="button"
      variant={"secondary"}
    >
      <AiOutlineDollar className="text-xl" />
      &nbsp;Log an Expense
    </Button>
  );

  const footer = (
    <>
      <DialogClose asChild>
        <Button
          variant="ghost"
          type="button"
          onClick={closeModal}
        >
          Cancel
        </Button>
      </DialogClose>
      <Button
        variant="secondary"
        type="button"
        disabled={isPending}
        onClick={handleSubmit}
      >
        {isPending ? (
          <RiLoader4Fill className="animate-spin text-2xl" />
        ) : (
          "Submit"
        )}
      </Button>
    </>
  );

  return (
    <ResponsiveDialog
      trigger={trigger}
      title="Log an Expense"
      footer={footer}
      open={showModal}
      onOpenChange={setShowModal}
      contentClassName="bg-[#FEF9F6] p-0"
    >
      <form
        className="flex w-full flex-col gap-4 p-4"
        onSubmit={async (e) => {
          e.preventDefault();
          await handleSubmit();
        }}
      >
        <Popover>
          <PopoverTrigger asChild>
            <Button
              variant={"outline"}
              className={cn(
                "h-12 w-full justify-start bg-white text-left font-normal",
                !date && "text-muted-foreground",
              )}
            >
              <CalendarIcon className="mr-2 h-4 w-4" />
              {date ? (
                format(date, "PPP")
              ) : (
                <span>Pick a date</span>
              )}
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-auto p-0">
            <Calendar
              mode="single"
              selected={date}
              onSelect={(date) => {
                return setDate(handleTimezone(date));
              }}
              disabled={(date) =>
                date > new Date() || date < new Date("1900-01-01")
              }
              initialFocus
            />
          </PopoverContent>
        </Popover>

        <MoneyInput
          name="Money Input"
          placeholder="Amount"
          value={amount}
          setValue={setAmount}
        />

        <Select onValueChange={(val) => setCategory(val)} defaultValue={category}>
          <SelectTrigger className="h-12 w-full bg-white text-black">
            <SelectValue placeholder="Select Category" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="feed">Feed</SelectItem>
            <SelectItem value="suplements">Suplements</SelectItem>
            <SelectItem value="medication">Medication</SelectItem>
            <SelectItem value="other">Other</SelectItem>
          </SelectContent>
        </Select>

        <Textarea
          className="w-full appearance-none rounded border bg-white px-2 py-2 text-black"
          value={memo}
          onChange={(e) => setMemo(e.target.value)}
          placeholder="Memo..."
        />
      </form>
    </ResponsiveDialog>
  );
};

export default ExpenseModal;

"use client";

import { useState } from "react";
import { trpc } from "../../utils/trpc";
import { MdOutlineEditNote } from "react-icons/md";
import toast from "react-hot-toast";
import { RiLoader4Fill } from "react-icons/ri";
import { handleTimezone } from "./date-utils";
import { format, startOfDay } from "date-fns";
import { useRouter } from "next/navigation";
import { Button } from "@components/ui/button";
import { Input } from "@components/ui/input";
import { Textarea } from "@components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@components/ui/select";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@components/ui/popover";
import { cn } from "@lib/utils";
import { CalendarIcon } from "@radix-ui/react-icons";
import { Calendar } from "@components/ui/calendar";
import { ResponsiveDialog, DrawerClose } from "@components/ui/responsive-dialog";

const LogModal = ({ flockId }: { flockId: string | undefined }) => {
  const [open, setOpen] = useState(false);
  const [date, setDate] = useState<Date>(new Date());
  const [count, setCount] = useState<number>();
  const [notes, setNotes] = useState<string>();
  const [breedId, setBreedId] = useState<string>();

  const router = useRouter();

  const utils = trpc.useUtils();

  const { mutateAsync: createLogMutation, isPending } =
    trpc.logs.createLog.useMutation({
      onSuccess: () => {
        utils.stats.getStats.invalidate();
        utils.stats.getExpenseStats.invalidate();
        router.refresh();
        toast.success("New log created!");
      },
    });

  const flockQuery = trpc.flocks.getFlock.useQuery({
    flockId: flockId as string,
  });

  function closeModal(): void {
    setOpen(false);
  }

  function resetFormValues(): void {
    setDate(new Date());
    setNotes(undefined);
    setCount(undefined);
    setBreedId(undefined);
  }

  async function createNewLog(
    flockId: string,
    date: Date,
    count: number,
    notes?: string,
  ): Promise<void> {
    await createLogMutation({
      flockId,
      date: startOfDay(date),
      count,
      notes,
      breedId,
    });
    closeModal();
    resetFormValues();
  }

  if (!flockId) {
    return null;
  }

  return (
    <ResponsiveDialog
      open={open}
      onOpenChange={setOpen}
      trigger={
        <Button
          className="mb-1 mr-1 h-10 w-full rounded px-4 py-2 shadow outline-none transition-all focus:outline-none"
          variant={"secondary"}
        >
          <MdOutlineEditNote className="text-2xl" />
          &nbsp;Log Eggs
        </Button>
      }
      title="Log Eggs"
      footer={
        <>
          <DrawerClose asChild>
            <Button variant="ghost" onClick={closeModal}>
              Cancel
            </Button>
          </DrawerClose>
          <Button
            variant="secondary"
            disabled={isPending}
            onClick={async () => {
              if (date && count) {
                await createNewLog(
                  flockId,
                  date,
                  count,
                  notes,
                );
              }
            }}
          >
            {isPending ? (
              <RiLoader4Fill className="animate-spin text-2xl" />
            ) : (
              "Submit"
            )}
          </Button>
        </>
      }
    >
      <form
        className="flex w-full flex-col gap-4 p-4"
        onSubmit={async (e) => {
          e.preventDefault();
          if (date && count) {
            await createNewLog(
              flockId,
              date,
              count,
              notes,
            );
          }
        }}
      >
        <Popover>
          <PopoverTrigger asChild>
            <Button
              variant={"outline"}
              className={cn(
                "h-12 w-full justify-start bg-white text-left font-normal",
                !date && "text-muted-foreground",
              )}
            >
              <CalendarIcon className="mr-2 h-4 w-4" />
              {date ? (
                format(date, "PPP")
              ) : (
                <span>Pick a date</span>
              )}
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-auto p-0">
            <Calendar
              mode="single"
              selected={date}
              onSelect={(date) => {
                return setDate(handleTimezone(date));
              }}
              disabled={(date) =>
                date > new Date() || date < new Date("1900-01-01")
              }
              initialFocus
            />
          </PopoverContent>
        </Popover>

        <Input
          type="tel"
          className="h-12 w-full rounded border bg-white px-2 py-2 text-black"
          required
          value={count}
          onChange={(e) => setCount(Number(e.target.value))}
          placeholder="Count"
        />

        <fieldset className="my-0">
          <Select onValueChange={(val) => setBreedId(val)}>
            <SelectTrigger className="h-12 w-full bg-white text-black">
              <SelectValue placeholder="Select bird(s) (Optional)" />
            </SelectTrigger>
            <SelectContent>
              {flockQuery.data?.breeds
                .filter((breed) => breed.averageProduction > 0)
                .map((breed) => {
                  return (
                    <SelectItem value={breed.id} key={breed.id}>
                      {breed.name && `${breed.name} - `}
                      {breed.breed}
                    </SelectItem>
                  );
                })}
            </SelectContent>
          </Select>
        </fieldset>

        <Textarea
          className="w-full rounded border bg-white px-2 py-2 text-black"
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          placeholder="Notes..."
        />
      </form>
    </ResponsiveDialog>
  );
};

export default LogModal;

"use client";

import * as React from "react";
import { CalendarIcon } from "@radix-ui/react-icons";
import { subDays, format, subMonths, parseISO } from "date-fns";
import { DateRange } from "react-day-picker";

import { cn } from "@lib/utils";
import { Button } from "@components/ui/button";
import { Calendar } from "@components/ui/calendar";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
  PopoverClose,
} from "@components/ui/popover";
import { usePathname, useSearchParams, useRouter } from "next/navigation";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@components/ui/select";

export function DatePickerWithRange({
  className,
}: React.HTMLAttributes<HTMLDivElement>) {
  // Get router, searchParams, and path from next/navigation
  const router = useRouter();
  const searchParams = useSearchParams();
  const path = usePathname();

  // These steps make sure the date range is set correctly when the page is refreshed
  const statsRange = searchParams.get("statsRange"); // Get statsRange from URL
  // Split statsRange into from and to
  const from = statsRange?.split(",")[0];
  const to = statsRange?.split(",")[1];

  // console.log("statsRange: ", statsRange);
  // console.log("from: ", parseISO(from!));
  // console.log("to: ", parseISO(to!));

  const [date, setDate] = React.useState<DateRange | undefined>({
    from: from ? parseISO(from) : subDays(new Date(), 7), // If from exists, use it, otherwise use 7 days ago
    to: to ? parseISO(to) : new Date(), // If to exists, use it, otherwise use today
  });
  const [previousDate, setPreviousDate] = React.useState<DateRange | undefined>(
    date, // Set previousDate to date
  );

  return (
    <div className={cn("grid gap-2", className)}>
      <Popover>
        <PopoverTrigger asChild>
          <Button
            id="date"
            variant={"outline"}
            className={cn(
              "w-full justify-start text-left font-normal sm:w-[300px]",
              !date && "text-muted-foreground",
            )}
          >
            <CalendarIcon className="mr-2 h-4 w-4" />
            {date?.from ? (
              date.to ? (
                <>
                  {format(date.from, "LLL dd, y")} -{" "}
                  {format(date.to, "LLL dd, y")}
                </>
              ) : (
                format(date.from, "LLL dd, y")
              )
            ) : (
              <span>Pick a date</span>
            )}
          </Button>
        </PopoverTrigger>
        <PopoverContent
          align="start"
          className="flex w-auto flex-col space-y-2 p-2"
        >
          <Select
            onValueChange={(value) =>
              setDate({
                from: subDays(new Date(), parseInt(value)),
                to: new Date(),
              })
            }
          >
            <SelectTrigger>
              <SelectValue placeholder="Select" />
            </SelectTrigger>
            <SelectContent position="popper">
              <SelectItem value="7">Last 7 Days</SelectItem>
              <SelectItem value="14">Last 14 Days</SelectItem>
              <SelectItem value="30">Last 30 Days</SelectItem>
              <SelectItem value="60">Last 60 Days</SelectItem>
            </SelectContent>
          </Select>
          <div className="hidden rounded-md border sm:block">
            <Calendar
              initialFocus
              mode="range"
              defaultMonth={date?.to ? subMonths(date.to, 1) : new Date()}
              selected={date}
              onSelect={setDate}
              numberOfMonths={2}
              toDate={new Date()}
            />
          </div>
          <div className="block rounded-md border sm:hidden" aria-hidden>
            <Calendar
              initialFocus
              mode="range"
              defaultMonth={date?.to}
              selected={date}
              onSelect={setDate}
              numberOfMonths={1}
              toDate={new Date()}
            />
          </div>
          <div className="flex w-full justify-end ">
            <PopoverClose asChild>
              <Button
                variant="ghost"
                type="button"
                role="cancel"
                onClick={() => {
                  // If previousDate exists, set date to previousDate which resets the calendar
                  if (previousDate) {
                    setDate(previousDate);
                  }
                }}
              >
                Cancel
              </Button>
            </PopoverClose>
            <PopoverClose asChild>
              <Button
                variant="secondary"
                type="button"
                role="submit"
                onClick={() => {
                  // If date is undefined, return
                  if (!date?.from || !date?.to) {
                    return;
                  }

                  setPreviousDate(date); // Set new previousDate for when people click cancel
                  // Get current searchParams
                  const curParams = new URLSearchParams(searchParams || "");

                  // Set statsRange to the new date range
                  curParams.set(
                    "statsRange",
                    `${format(date.from, "yyyy-MM-dd")},${format(
                      date.to,
                      "yyyy-MM-dd",
                    )}`,
                  );

                  // Replace the URL with the new searchParams
                  router.replace(`${path}?${curParams.toString()}`);
                }}
              >
                Done
              </Button>
            </PopoverClose>
          </div>
        </PopoverContent>
      </Popover>
    </div>
  );
}

